<script>
import {config} from '@/config'
import VueApexCharts from 'vue-apexcharts'
import { askForPermissioToReceiveNotifications } from '~/plugins/push.js'

export default {
  components: {
    'apexcharts': VueApexCharts
  },
  name: 'ZerDa',
  head () {
    return {
      title: 'Zer da Musika Gida Morea? - ' + config.title
    }
  },
  data() {
    return {
      loaderStatus: false,
      subStatus: false,
      config,
      region: null,
      regionLoad: false,
      regionsArray: [],
      regionData: [],
      chartOptions: {
        chart: {
          id: 'vuechart-example',
          height: 100,
        },
        xaxis: {
          categories: ['0-20%', '20%-40%', '40%-60%', '60%-80%', '80%-100%']
        },
        dataLabels: {
          style: {
            colors: ['#ffffff']
          }
        },
        fill: {
          colors: ['#7b3772', '#7b3772', '#7b3772']
        },
        colors:['#7b3772', '#E91E63', '#9C27B0']

      },
      series: [{
        name: 'Taldeak',
        data: []
      }]
    }
  },
  created(){
    // Push notifications
    setTimeout( () => {
      let pushStatus = window.localStorage.getItem('__gidamorea-push')
      if(pushStatus == 'true') { this.subStatus = true } else { this.subStatus = false }
    },10)

    this.loaderStatus = true
    var dataArray = []

    this.$axios.get(config.apiURL + '/bands-no-pagination')
      .then(res => {
        this.regionsArray = res.data.bands
        this._createGraphic()
      })

      //map
      if(process.browser){
        setTimeout( () => {

          let region = document.querySelectorAll('.region'),
            infobox = document.getElementById('info-box')

          for (var i = 0; i < region.length; i++) {
            region[i].addEventListener('click', (e) => {
              for (var j = 0; j < region.length; j++) {
                if(region[j].classList.contains('isActive')){
                  region[j].classList.remove('isActive')
                }
              }

              infobox.style.display = "block";
              let rect = document.querySelector('.map__map').getBoundingClientRect();
              let x = e.clientX - rect.left;
              let y = e.clientY - rect.top;
              infobox.style.top = y - infobox.offsetHeight + 'px'
              infobox.style.left = x - infobox.offsetWidth/2 + 'px'
              e.target.classList.add('isActive')
              this._handleRegion(e.target.classList[1], e.target.dataset.name, infobox)
            })
          }
        },10)
      }
  },
  methods: {
    _handleRegion(e, fullName, infobox){
      if(process.browser){
        this.regionLoad = false
        infobox.innerHTML = ''
        setTimeout(() => {
          this.regionLoad = true
          const result = this.regionsArray.filter(region => {
            return region.country == e
          })
          this.region = fullName
          this.regionData = result

          infobox.innerHTML = `<strong>${fullName} </strong>
            - (${this.regionData.length} talde)`

        }, 10)
      }
    },
    _createGraphic(){
      let count_0  = 0,
        count_20 = 0,
        count_40 = 0,
        count_60 = 0,
        count_80 = 0

      this.regionsArray.forEach(band => {
        if(band.percent <= 20)
          count_0++
        else if(band.percent > 20 && band.percent <= 40)
          count_20++
        else if(band.percent > 40 && band.percent <= 60)
          count_40++
        else if(band.percent > 60 && band.percent <= 80)
          count_60++
        else if(band.percent > 80)
          count_80++
      })

      this.series[0].data = [count_0, count_20, count_40, count_60, count_80]
      //this.series[0].data = [23, 25, 12, 6, 3]
      this.loaderStatus = false
    },
    async _handleChange(e) {

      localStorage.setItem('__gidamorea-push', this.subStatus)

       const getToken = await askForPermissioToReceiveNotifications()
       try {
         let postData = {
           mode: (this.subStatus == true) ? 'subscribe' : 'unsubscribe',
           token: getToken,
         }
         this.$axios.post(config.apiURL + '/firebase', postData).then()
       }
       catch(err) {
         console.log(err)
       }
    }
  }
}
</script>
