// the switcher css$
/* The switch - the box around the slider */
.switch {
  position: relative;
  display: block;
  width: 40px;
  height: 20px;
  margin-left: 0;
  margin-top: 5px;
}

/* Hide default HTML checkbox */
.switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

/* The slider */
.switcher {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba($primary,.15);
  transition: .2s;
}

.switcher:before {
  position: absolute;
  content: "";
  height: 12px;
  width: 12px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  transition: .2s;
}

input:checked + .switcher {
  background-color: rgba($primary,1);
  &.switcher:before {
    background-color: white;
  }
}

input:focus + .switcher {
  box-shadow: 0 0 1px rgba(black,.1);
}

input:checked + .switcher:before {
  transform: translateX(20px);
}

/* Rounded sliders */
.switcher.round {
  border-radius: 20px;
}

.switcher.round:before {
  border-radius: 50%;
}

@keyframes apiCall {
  0% {
    transform: scale(0);
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}
</style>
